    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}{{product.name}} - La boutique française{% endblock %}

{% block content %}

    <div class=\"row\">
        <div class=\"col-md-5\">
           <img src=\"/uploads/{{product.illustration}}\" alt=\"{{product.name}}\" class=\"img-fluid\"> 
        </div>
        <div class=\"col-md-7 my-auto\">
            <h3>{{product.name}}</h3>
            <p>{{product.subtitle}}</p>
            <span class=\"product-page-prix\">{{(product.prix / 100) |number_format('2')}} €</span>
            <hr>
            <p>{{product.description}}</p>
            <a href=\"{{path('add_to_cart',{'id' :product.id})}}\" class=\"btn btn-primary\">Ajouter au panier</a>
        </div>
    </div>
    <hr class=\"border border-black border-3 opacity-75\">
    <div class=\"text-center mt-5\">
        <h3>Nos meilleurs ventes</h3>
        <p>Découvrez nos articles les plus vendus</p>
    </div>
    <div class=\"row\">
    {% for product in products %}
        <div class=\"col-md-3\">
        {% include 'products/single_product.html.twig' %}
        </div>
    {% endfor %}
        </div><!-- /.row -->



    


{% endblock %}", "products/show.html.twig", "/var/www/html/laboutiquefrancaise/templates/products/show.html.twig");
